import UIKit

class APGroupMembersViewController: APBaseViewController, UICollectionViewDataSource, UICollectionViewDelegate, UITableViewDataSource, UITableViewDelegate, APFriendViewCellDelegate, APTableViewCellDelegate {

    @IBOutlet weak var membersCollectionView: UICollectionView!
    @IBOutlet weak var friendsTableView: UITableView!
    @IBOutlet weak var nextLabel: UILabel!
    
    @IBOutlet weak var heightNextConstraint: NSLayoutConstraint!
    @IBOutlet weak var heightMembersConstraint: NSLayoutConstraint!
    
    private var selectedFriends: [APUser] = []
    private var friends: [APUser] = []
    private var members: [APUser] = []
    
    override func viewDidLoad() {
        super.viewDidLoad()

        // Do any additional setup after loading the view.
        for i in 0...9 {
            let user = APUser()
            user.username = "\(i)"
            friends.append(user)
        }
    }

    override func didReceiveMemoryWarning() {
        super.didReceiveMemoryWarning()
        // Dispose of any resources that can be recreated.
    }
    
    func showUserProfile() {
        let storyboard = UIStoryboard(name: "Profile", bundle: nil)
        let controller = storyboard.instantiateViewController(withIdentifier: "APProfileViewController") as! APProfileViewController
        navigationController?.pushViewController(controller, animated: true)
    }
    
