    // MARK: - IBAction
    @IBAction func nextButtonPressed(_ sender: UIButton) {
        performSegue(withIdentifier: "APGroupNameViewController", sender: nil)
    }
    
    // MARK: - UICollectionViewDataSource
    func collectionView(_ collectionView: UICollectionView, numberOfItemsInSection section: Int) -> Int {
        return selectedFriends.count
    }
    
    func collectionView(_ collectionView: UICollectionView, cellForItemAt indexPath: IndexPath) -> UICollectionViewCell {
        let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "APMemberViewCell", for: indexPath) as! APMemberViewCell
        return cell
    }
    
    // MARK: - UICollectionViewDelegate
    func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
        showUserProfile()
    }
    
    // MARK: - UITableViewDataSource
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return friends.count
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "APFriendViewCell", for: indexPath) as! APFriendViewCell
        cell.delegate = self
        cell.actionDelegate = self
        let user = friends[indexPath.row]
        if selectedFriends.contains(user) {
            cell.isSelection = true
        } else {
            cell.isSelection = false
        }
        cell.user = user
        return cell
    }
    
    // MARK: - UITableViewDelegate
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        //showUserProfile()
        let cell = tableView.cellForRow(at: indexPath) as! APFriendViewCell
        cell.isSelection = !cell.isSelection
        didSelectFriend(friend: cell.user, selection: cell.isSelection)
    }
    
    func tableView(_ tableView: UITableView, heightForRowAt indexPath: IndexPath) -> CGFloat {
        return 68
    }
    
    func tableView(_ tableView: UITableView, heightForHeaderInSection section: Int) -> CGFloat {
        return 0
    }
    
    func tableView(_ tableView: UITableView, viewForHeaderInSection section: Int) -> UIView? {
        let view = tableView.dequeueReusableCell(withIdentifier: "APChatReadCell") as! APChatReadCell
        view.readLabel.text = "Friends (\(friends.count))"
        return view
    }
    
    // MARK: - APFriendViewCellDelegate
    func didSelectFriend(friend: APUser, selection: Bool) {
        if selection == true {
            selectedFriends.append(friend)
        } else {
            selectedFriends = selectedFriends.filter{ $0 != friend }
        }
        if selectedFriends.count == 0 {
            nextLabel.text = ""
            heightNextConstraint.constant = 0
            heightMembersConstraint.constant = 0
        } else {
            heightMembersConstraint.constant = 52
            if selectedFriends.count == 1 {
                heightNextConstraint.constant = 0
                nextLabel.text = "Chat with Bernice"
            } else {
                heightNextConstraint.constant = 64.0
                nextLabel.text = "Chat with Bernice and \(selectedFriends.count - 1) others"
            }
        }
        membersCollectionView.reloadData()
    }
    
    // MARK: - APTableViewCellDelegate
    func didPressThumbButton(_ cell: APTableViewCell) {
        showUserProfile()
    }
}
